package datatypes;

public class CharDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch = 'A';
		
		System.out.println(ch);
		
		ch = 'a';
		
		System.out.println(ch);
		
		char abc = '5';
		
		System.out.println(abc);
		
		abc = '&';
		System.out.println(abc);
		ch = ' ';
		System.out.print(ch);
		
		System.out.println(abc);
	}

}
