package datatypes;

public class DataTypeConversion2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch = 'f';
		short sh = 67;
		byte bt = 34;
		int in = 340;
		float fl = 45.34f;
		double db = 23.33;		
		ch = 65;		
		System.out.println(ch);
		ch = 'f';		
		in = ch;		
		System.out.println(ch);
		System.out.println(in);
		
		

	}

}
